C语言: #include &lt;stdio.h&gt; #include &lt;time.h&gt; //给计算结果一个数组，并指定大小为4096 //第一次写程序使用的字符串保存大数，但是有个问题，需要char到int来回转换，影响计算性能。 //所以使用数组来保存数据，方便计算 int jieguo[40960] = {0}; //数组长度 int len = 0; //fac为计算阶乘的函数，计算结果存储在jieguo[4096]数组中 int fac(int n) { int v = 0; int f = 0; //初始化jieguo数组和数组长度 jieguo[0] = 1; len = 1; for(int i = 1; i &lt;= n; i++) { for(int j = len - 1; j &gt;= 0; j--) { v = i * jieguo[j]; jieguo[j] = v % 10; v = v / 10; f = j + 1; //如果v大于0，说明还要进位，所以将进位的数加上原来的数，判断进位后存储。 //为了提高效率，使用for进行循环 for(; v &gt; 0 &amp;&amp; f &lt; len; f++) { v = v + jieguo[f]; jieguo[f] = v % 10; v = v / 10; } //如果这时f等于数组的长度len并且v还大于0，说明需要增加jieguo数组的长度 //为了提高效率，仍然使用for进行循环 for(; v &gt; 0;) { jieguo[len] = v % 10; v = v / 10; len++; if(len &gt; 40960 - 2) { //这个时候说明数组长度不够，需要增加jieguo后面的数值 //计算100的阶乘的数组长度为158，计算1000的阶乘的数组长度为2568。 //可以根据实际需要进行设置。 return 1; } } }	} return 0; } //下面写主函数 int main(void) { int num; printf(&#34;请输入需要计算阶乘的数字:&#34;); scanf(&#34;%d&#34;, &amp;num); //计算阶乘结果 int r = fac(num); //如果返回1,则说明数组长度不够，需要增加长度 if(r == 1) { printf(&#34;计算结果太大，请增加数组长度！
&#34;); return 0; } printf(&#34;%d的阶乘为：&#34;, num); for(int i = len - 1; i &gt;= 0; i--) { printf(&#34;%d&#34;, jieguo[i]); } printf(&#34;
&#34;); printf(&#34;%d
&#34;, len); printf(&#34;运行时间:%.2lf秒
&#34;, (double)clock() / CLOCKS_PER_SEC); printf(&#34;
&#34;); return 0;	} golang: package main import &#34;fmt&#34; import &#34;time&#34; const m int = 40960 func fac(n int) ([m]int, int) { var jieguo [m]int var len int var v, f = 0, 0 jieguo[0] = 1 len = 1 for i := 1; i &lt;= n; i++{ for j := len - 1; j &gt;= 0; j--{ v = i * jieguo[j] jieguo[j] = v % 10 v = v / 10 f = j + 1 for ; v &gt; 0 &amp;&amp; f &lt; len; f++ { v = v + jieguo[f] jieguo[f] = v % 10 v = v / 10 } for ; v &gt; 0; len++ { jieguo[len] = v % 10 v = v / 10 if len &gt; m - 2 { fmt.Println(&#34;error!&#34;) return jieguo, len } } } } return jieguo, len } func main() { a := 10000 startTime := time.Now() f, len := fac(a) fmt.Printf(&#34;%d的阶乘为：&#34;, a) for i := len - 1; i &gt;= 0; i-- { fmt.Print(f[i]) } fmt.Print(&#34;
&#34;) fmt.Println(len) t := time.Since(startTime) fmt.Println(t.Seconds()) }

php截取字符串有两个函数:
一个是substr()，这个用来截取全字母的字符串，截取UTF-8的汉字就会出错；
另一个是mb_substr()，这个用来截取汉字，但一定要注明编码方式：mb_substr(&lsquo;我的祖国是中国&rsquo;, 0, 4, &lsquo;utf-8&rsquo;);

使用开源项目editor.md我的gitee仓库下也有。
安装 # npm install editor.md
使用 &lt;!DOCTYPE html&gt;&lt;html&gt;&lt;head&gt;&lt;meta charset=&#34;utf-8&#34;&gt;&lt;link rel=&#34;stylesheet&#34; href=&#34;editor.md/css/editormd.min.css&#34; /&gt;&lt;title&gt;Markdown 编辑在线器&lt;/title&gt;&lt;/head&gt;&lt;body&gt;&lt;div id=&#34;text&#34; style=&#34;width: 100%;&#34;&gt;&lt;div id=&#34;editor&#34; style=&#34;height: 100%;&#34;&gt;&lt;!-- Tips: Editor.md can auto append a `&lt;textarea&gt;` tag --&gt;&lt;textarea style=&#34;display:none;&#34; placeholder=&#34;欢迎使用&#34;&gt;&lt;/textarea&gt;&lt;/div&gt;&lt;/div&gt;&lt;script src=&#34;jquery.min.js&#34;&gt;&lt;/script&gt;&lt;script src=&#34;editor.md/editormd.min.js&#34;&gt;&lt;/script&gt;&lt;script type=&#34;text/javascript&#34;&gt;// 设置div与窗口高度一致window.onload = function () {var h = window.innerHeight - 25 ; //获取当前窗口的高度; document.getElementById(&#34;text&#34;).style.height = h + &#34;px&#34;; //设置div的高度}// 设置textarea参数$(function() {var h = window.innerHeight - 25var editor = editormd(&#34;editor&#34;, {// width: &#34;100%&#34;,height: h,saveHTMLToTextarea : true,// markdown: &#34;xxxx&#34;, // dynamic set Markdown textpath : &#34;editor.md/lib/&#34; // Autoload modules mode, codemirror, marked... dependents libs path});});&lt;/script&gt;&lt;/body&gt;&lt;/html&gt;

